document.addEventListener("DOMContentLoaded",function(){const t=document.querySelector(".site-header"),e=document.querySelector(".menu-toggle"),n=document.querySelector(".site-nav");if(!t||!e||!n)return;e.addEventListener("click",function(n){n.stopPropagation();const s=t.classList.toggle("menu-open");e.setAttribute("aria-expanded",s),e.textContent=s?"✕":"☰"}),n.addEventListener("click",function(e){e.stopPropagation()}),document.addEventListener("click",function(){t.classList.remove("menu-open"),e.setAttribute("aria-expanded","false"),e.textContent="☰"});const s=document.querySelectorAll(".submenu-toggle");s.forEach(e=>{e.addEventListener("click",function(t){t.preventDefault(),t.stopPropagation();const n=e.closest(".nav-item");n.classList.toggle("submenu-open")})})})