document.addEventListener("DOMContentLoaded",function(){const t=document.querySelector(".site-header"),e=document.querySelector(".menu-toggle");if(!t||!e)return;e.addEventListener("click",function(n){n.stopPropagation();const s=t.classList.toggle("menu-open");e.textContent=s?"✕":"☰"}),document.addEventListener("click",function(){t.classList.remove("menu-open"),e.textContent="☰"}),document.querySelectorAll(".submenu-toggle").forEach(e=>{e.addEventListener("click",function(t){t.preventDefault(),t.stopPropagation(),e.closest(".nav-item").classList.toggle("submenu-open")})})})