document.addEventListener("DOMContentLoaded",function(){const t=document.querySelector(".site-header"),e=document.querySelector(".menu-toggle"),n=document.querySelector(".site-nav");if(!t||!e||!n)return;e.addEventListener("click",function(n){n.stopPropagation();const s=t.classList.toggle("menu-open");e.setAttribute("aria-expanded",s),e.textContent=s?"✕":"☰"}),n.addEventListener("click",function(e){e.stopPropagation()}),document.addEventListener("click",function(){t.classList.contains("menu-open")&&(t.classList.remove("menu-open"),e.setAttribute("aria-expanded","false"),e.textContent="☰")})});const featured=document.querySelector(".home-featured");if(featured){const e=new IntersectionObserver(([e])=>{e.isIntersecting&&featured.classList.add("is-visible")},{threshold:.15});e.observe(featured)}